<?php

use PHPUnit\Framework\TestCase;
use TBela\CSS\Element;
use TBela\CSS\Element\AtRule;
use TBela\CSS\Element\Stylesheet;
use TBela\CSS\Compiler;
use TBela\CSS\Parser;
use TBela\CSS\Renderer;

final class DataUri extends TestCase
{
    /**
     * @param string $parsed
     * @param string $expected
     * @dataProvider DataUriProvider
     */
    public function testDataUri($parsed, $expected)
    {
        $this->assertEquals(
            $expected,
            $parsed
        );
    }

    /**
     * @param string $parsed
     * @param string $expected
     * @dataProvider DataUriAstProvider
     */
    public function testDataUriAst($parsed, $expected)
    {
        $this->assertEquals(
            $expected,
            $parsed
        );
    }

    public function DataUriProvider () {

        $parser = new TBela\CSS\Parser;

        $parser->load(__DIR__.'/../data/uri.css');

        $data = [];

        $data[] = [(string) $parser->parse(),
            '.test {
 background-color: red
}
.t228__white-black .ya-share2__container_size_m .ya-share2__item_service_facebook .ya-share2__icon {
 background-image: url(data:image/svg+xml;base64,)
}
.test {
 color: red
}'];

        return $data;
    }

    public function DataUriAstProvider () {

        $parser = new TBela\CSS\Parser;

        $parser->load(__DIR__.'/../data/uri.css');

        $data = [];

        $data[] = [(new Renderer())->renderAst($parser->getAst()),
            '.test {
 background-color: red
}
.t228__white-black .ya-share2__container_size_m .ya-share2__item_service_facebook .ya-share2__icon {
 background-image: url(data:image/svg+xml;base64,)
}
.test {
 color: red
}'];

        return $data;
    }
}

